mapper.select:
SELECT COUNT(*) FROM (SELECT 1 as TYPE_ID , ETBL1.ORDER_ID , SUBSTR(ETBL1.START_DATE,6,5) AS DATE , SUBSTR(ETBL1.END_DATE,6,5) AS END_DATE , SUBSTR(ETBL1.DATE_MODIFIED,6,5) AS DATE_MODIFIED , ETBL1.TOTAL_PAYMENT , ETBL1.USER_ID , ETBL1.RESULT , ETBL1.TOPIC , ETBL2.NAME AS USER_NAME , ETBL2.EMAIL AS USER_EMAIL , ETBL3.NAME AS MANAGER_ID , ETBL4.NAME AS WRITER_ID , ETBL5.REFUNDED AS REFUNDED FROM TBLESSAY ETBL1 LEFT JOIN TBLUSERS ETBL2 ON ETBL1.USER_ID = ETBL2.USER_ID LEFT JOIN TBLUSERS ETBL3 ON ETBL1.MANAGER_ID = ETBL3.USER_ID LEFT JOIN TBLUSERS ETBL4 ON ETBL1.WRITER_ID = ETBL4.USER_ID LEFT JOIN TBLORDER ETBL5 ON ETBL1.ORDER_ID = ETBL5.ORDER_ID WHERE ETBL1.STATUS = 3 AND ETBL1.VALID_FLAG = 1
AND ETBL1.ORDER_ID = #{search_keyword}
AND ETBL2.EMAIL LIKE CONCAT('%', #{search_keyword}, '%')
AND ETBL4.NAME LIKE CONCAT('%', #{search_keyword}, '%')
AND DATE_FORMAT(ETBL1.START_DATE,'%Y-%m') = #{search_keyword}
UNION ALL SELECT 2 as TYPE_ID , RTBL1.ORDER_ID , SUBSTR(RTBL1.START_DATE,6,5) AS DATE , SUBSTR(RTBL1.END_DATE,6,5) AS END_DATE , SUBSTR(RTBL1.DATE_MODIFIED,6,5) AS DATE_MODIFIED , RTBL1.TOTAL_PAYMENT , RTBL1.USER_ID , RTBL1.RESULT , RTBL1.TOPIC , RTBL2.NAME AS USER_NAME , RTBL2.EMAIL AS USER_EMAIL , RTBL3.NAME AS MANAGER_ID , RTBL4.NAME AS WRITER_ID , RTBL5.REFUNDED AS REFUNDED FROM TBLREVISION RTBL1 LEFT JOIN TBLUSERS RTBL2 ON RTBL1.USER_ID = RTBL2.USER_ID LEFT JOIN TBLUSERS RTBL3 ON RTBL1.MANAGER_ID = RTBL3.USER_ID LEFT JOIN TBLUSERS RTBL4 ON RTBL1.WRITER_ID = RTBL4.USER_ID LEFT JOIN TBLORDER RTBL5 ON RTBL1.ORDER_ID = RTBL5.ORDER_ID WHERE RTBL1.STATUS = 3 AND RTBL1.VALID_FLAG = 1
AND RTBL1.ORDER_ID = #{search_keyword}
AND RTBL2.EMAIL LIKE CONCAT('%', #{search_keyword}, '%')
AND RTBL4.NAME LIKE CONCAT('%', #{search_keyword}, '%')
AND DATE_FORMAT(RTBL1.START_DATE,'%Y-%m') = #{search_keyword}
) A ORDER BY END_DATE ASC

SELECT * FROM (SELECT 1 as TYPE_ID , ETBL1.ORDER_ID , SUBSTR(ETBL1.START_DATE,6,8) AS DATE , SUBSTR(ETBL1.END_DATE,6,8) AS END_DATE , SUBSTR(ETBL1.DATE_MODIFIED,6,8) AS DATE_MODIFIED , ETBL1.TOTAL_PAYMENT , ETBL1.USER_ID , ETBL1.RESULT , ETBL1.TOPIC , ETBL2.NAME AS USER_NAME , ETBL2.EMAIL AS USER_EMAIL , ETBL3.NAME AS MANAGER_ID , ETBL4.NAME AS WRITER_ID , ETBL5.REFUNDED AS REFUNDED FROM TBLESSAY ETBL1 LEFT JOIN TBLUSERS ETBL2 ON ETBL1.USER_ID = ETBL2.USER_ID LEFT JOIN TBLUSERS ETBL3 ON ETBL1.MANAGER_ID = ETBL3.USER_ID LEFT JOIN TBLUSERS ETBL4 ON ETBL1.WRITER_ID = ETBL4.USER_ID LEFT JOIN TBLORDER ETBL5 ON ETBL1.ORDER_ID = ETBL5.ORDER_ID WHERE ETBL1.STATUS = 3 AND ETBL1.VALID_FLAG = 1
AND ETBL1.ORDER_ID = #{search_keyword}
AND ETBL2.EMAIL LIKE CONCAT('%', #{search_keyword}, '%')
AND ETBL4.NAME LIKE CONCAT('%', #{search_keyword}, '%')
AND DATE_FORMAT(ETBL1.START_DATE,'%Y-%m') = #{search_keyword}
UNION ALL SELECT 2 as TYPE_ID , RTBL1.ORDER_ID , SUBSTR(RTBL1.START_DATE,6,8) AS DATE , SUBSTR(RTBL1.END_DATE,6,8) AS END_DATE , SUBSTR(RTBL1.DATE_MODIFIED,6,8) AS DATE_MODIFIED , RTBL1.TOTAL_PAYMENT , RTBL1.USER_ID , RTBL1.RESULT , RTBL1.TOPIC , RTBL2.NAME AS USER_NAME , RTBL2.EMAIL AS USER_EMAIL , RTBL3.NAME AS MANAGER_ID , RTBL4.NAME AS WRITER_ID , RTBL5.REFUNDED AS REFUNDED FROM TBLREVISION RTBL1 LEFT JOIN TBLUSERS RTBL2 ON RTBL1.USER_ID = RTBL2.USER_ID LEFT JOIN TBLUSERS RTBL3 ON RTBL1.MANAGER_ID = RTBL3.USER_ID LEFT JOIN TBLUSERS RTBL4 ON RTBL1.WRITER_ID = RTBL4.USER_ID LEFT JOIN TBLORDER RTBL5 ON RTBL1.ORDER_ID = RTBL5.ORDER_ID WHERE RTBL1.STATUS = 3 AND RTBL1.VALID_FLAG = 1
AND RTBL1.ORDER_ID = #{search_keyword}
AND RTBL2.EMAIL LIKE CONCAT('%', #{search_keyword}, '%')
AND RTBL4.NAME LIKE CONCAT('%', #{search_keyword}, '%')
AND DATE_FORMAT(RTBL1.START_DATE,'%Y-%m') = #{search_keyword}
) A ORDER BY END_DATE DESC
LIMIT ${record_count_per_page} OFFSET ${first_index}

mapper.insert:

	SELECT IFNULL(MAX(ORDER_ID), 0) + 1 FROM TBLORDER

INSERT INTO TBLORDER ( 
	ORDER_ID
	, USER_ID
	, USER_EMAIL
	, TYPE_ID
	, TOTAL_PAYMENT
	, DAYS
	, DATE
	, START_DATE
	, END_DATE
	, DATE_MODIFIED
	, STATUS
	, VALID_FLAG
	, DELETE_FLAG
) VALUES (
	#{order_id}
	, #{user_id}
	, #{user_email}
	, #{type_id}
	, #{total_payment}
	, #{days}
	, DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
	, DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
	, #{end_date}
	, DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
	, 1
	, 1
	, 0
)

mapper.update:
UPDATE
	TBLORDER
SET
	DATE_MODIFIED = DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')

		, TOTAL_PAYMENT = #{total_payment}


		, DAYS = #{days}


		, END_DATE = #{end_date}

WHERE 
	ORDER_ID = #{order_id}
